mapper.select:
SELECT A.IDX , A.ID , A.FILE_NAME , A.STATUS , DATE_FORMAT(A.FIRST_DATE, '%Y-%m-%d %H:%i:%s') AS FIRST_DATE , DATE_FORMAT(A.RENEWAL_DATE, '%Y-%m-%d %H:%i:%s') AS RENEWAL_DATE , B.NAME , B.EMAIL , B.PHONE FROM finder A , user B WHERE 1 = 1 AND B.ID = A.ID
AND A.ID = #{id}
ORDER BY IDX DESC

mapper.update:
UPDATE finder
   SET ACCURACY = #{accuracy}
   	 , STATUS = 'Y'
   	 , RENEWAL_DATE = NOW()
   	 , MISSINGS_ID = #{missings_id}
 WHERE 1 = 1
   AND IDX = #{idx}

mapper.select:
SELECT A.IDX , B.SEX , B.NAME , B.OCCR_AGE , B.CURRENT_AGE , B.OCCR_DATE , B.OCCR_ADDRESS FROM finder A , missings B WHERE 1 = 1 AND B.ID = A.MISSINGS_ID
AND A.ID = #{id}
ORDER BY IDX DESC
